-->
<!-- script -->
<script lang="ts">
  /* typescript */
  import type { ProjectContentType } from '../../../types/common'
  /* svelte imports */
  import { createEventDispatcher } from 'svelte'

  /* props */
  export let id: ProjectContentType
  export let title: string
  export let path: string
  /* dispatchers */
  const dispatch = createEventDispatcher<{ click : { path: string, id: ProjectContentType } }>()
  /* refs */
  let svgImageRef: SVGImageElement | null = null
  /* functions */
  function onDoubleClick() { dispatch('click', { path, id }) }
  function onMouseEnter() {
    if (!svgImageRef) { return }
    svgImageRef.setAttribute('href', 'data:image/png;base64,')
  }
  function onMouseLeave() {
    if (!svgImageRef) { return }
    svgImageRef.setAttribute('href', 'data:image/png;base64,')
  }
</script>

<!-- template -->
<button
class="container"
on:dblclick={onDoubleClick}
on:mouseenter={onMouseEnter}
on:mouseleave={onMouseLeave}
>
  <svg width="67px" height="67px" viewBox="0 0 1024 1024" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
    <image
      bind:this={svgImageRef}
      width="1024"
      height="1024"
      href="data:image/png;base64,"
    />
  </svg>
  <span class="text">{title}</span>
</button>

