<!-- style -->
<script lang="ts">
  /* svelte imports */
  import { createEventDispatcher } from 'svelte'
  import { draggable } from '@neodrag/svelte'

  /* exports */
  export let text: string
  export let url: string
  export let gridRow: string = 'auto'
  export let gridColumn: string = 'auto'
  /* dispatchers */
  const dispatch = createEventDispatcher()
  /* functions */
  const onDoubleClick = () => {
    dispatch('open')
    window.open(url, '_blank') || window.location.replace(url)
  }
</script>

<!-- template -->
<button
  class="desktop-item"
  use:draggable={{ bounds: 'parent' }}
  on:dblclick={onDoubleClick}
  style:grid-row={gridRow}
  style:grid-column={gridColumn}
>
  <svg width="64px" height="64px" viewBox="0 0 1024 1024" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
    <image width="1024" height="1024" href="data:image/png;base64,"
    />
  </svg>
  <span class="icon-name">
    {text}
  </span>
  <div class="external-container">
    <svg
