<!-- style -->
<script lang="ts">
  /* imports */
  import { createEventDispatcher } from 'svelte'
  import { draggable } from '@neodrag/svelte'
  import ResizableModal from '../modal/resizable.modal.svelte'

  /* exports */
  export let text: string
  export let modalWidth: string = '600px'
  export let modalHeight: string = '450px'
  export let modalTop: string | undefined = undefined
  export let modalLeft: string | undefined = undefined
  export let modalRight: string | undefined = undefined
  export let modalBottom: string | undefined = undefined
  export let uniqueId: number
  export let mainColor: string = '#312454'
  export let openModal: boolean = false
  export let gridRow: string = 'auto'
  export let gridColumn: string = 'auto'
  /* state */
  let visible: boolean = false
  /* dispatchers */
  const dispatch = createEventDispatcher()
  /* reactive */
  $: if (openModal) { open() }
  /* functions */
  const onDoubleClick = () => { open() }
  function close() {
    dispatch('close')
    visible = false
  }
  function open() {
    dispatch('open')
    visible = true
  }
</script>

<!-- template -->
<button
  class="desktop-item"
  use:draggable={{ bounds: 'parent' }}
  on:dblclick={onDoubleClick}
  style:grid-row={gridRow}
  style:grid-column={gridColumn}
>
  <svg width="60px" height="60px" viewBox="0 0 1024 1024" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
    <image
      width="1024"
      height="1024"
      href="data:image/png;base64,"
    />
  </svg>    
  <span class="icon-name">
    {text}
  </span>
</button>
{#if visible}
  <ResizableModal
    initialWidth={modalWidth}
    initialHeight={modalHeight}
    on:closeModal={close}
    uniqueId={uniqueId}
    mainColor={mainColor}
    top={modalTop}
    left={modalLeft}
    right={modalRight}
    bottom={modalBottom}
    text={text}
  >
    <slot />
  </ResizableModal>
{/if}

<!-- style -->
<style>
  .desktop-item {
    /* size */
    max-width: 100px;
    max-height: 100px;
    aspect-ratio: 1 / 1;
    /* flex */
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    /* margins */
    padding-left: 16px;
    padding-right: 16px;
    /* color */
    background-color: transparent;
  }
  .desktop-item:hover, .desktop-item:focus {
    /* color */
    background-color: var(--color-highlight-hover);
    box-shadow: inset 0 0 0 1px var(--color-highlight);
    /* animation */
    transition: background-color 220ms ease-out;
  }
  .icon-name {
    /* text */
    font-size: 18px;
    font-weight: 400;
    text-shadow: 1px 1px var(--color-neon-blue), -1px -1px var(--color-neon-violet), -1px 0px var(--color-neon-yellow);
    /* color */
    color: var(--color-black);
    /* select */
    user-select: none;
    /* margins */
    margin-top: 2px;
  }
  /* desktop */
  @media only screen and (min-width: 1280px) {
    .desktop-item {
      /* size */
      max-width: 84px;
      max-height: 84px;
    }
  }
  /* large desktop */
  @media only screen and (min-width: 1920px) {
    .desktop-item {
      /* size */
      max-width: 100px;
      max-height: 100px;
    }
  }
</style>